"use strict";function deliveryDate(e="get"){Date.prototype.addDays=function(e){const t=new Date(this.valueOf());return t.setDate(t.getDate()+e),t};const t=(new Date).addDays(15),a=t.getDay(),r=["Monday","Tuesday","Wednesday","Thursday","Friday","Satuday","Sunday"][t.getDay()],y=["January","February","March","April","May","June","July","August","September","October","November","December"][t.getMonth()];if("get"==e)return{day:r,month:y,dayNum:a};if("set"==e){[...document.querySelectorAll(".delivery_date")].map((e=>{e.textContent=`${r}, ${y} ${a}`}))}}